  const [tooltipVisible, setTooltipVisible] = useState(false);
  const [tooltipPosition, setTooltipPosition] = useState({ x: 0, y: 0 });
  const [tooltipContent, setTooltipContent] = useState("");
  const handleProvinceClick = (event) => {
    const province = event.target.getAttribute("id");
    const boundingRect = event.target.getBoundingClientRect();
    let menuPos = {};

    if (province === "West_Coast" || "North_Canterbury" || "South_Canterbury") {
      // Position menu at the center of the province element.
      menuPos = {
        x: boundingRect.left + boundingRect.width / 2 + window.pageXOffset,
        y: boundingRect.top + boundingRect.height / 2 + window.pageYOffset,
      };
    } else {
      const offsetY =
        boundingRect.y + 210 - window.innerHeight < 0
          ? 0
          : boundingRect.y + 210 - window.innerHeight;
      const offsetX =
        boundingRect.x + 200 - window.innerWidth < 0
          ? 0
          : boundingRect.x + 200 - window.innerWidth;
      menuPos = {
        x: boundingRect.x + window.pageXOffset - offsetX,
        y: boundingRect.y + window.pageYOffset - offsetY,
      };
    }

    setSelectedProvince(province);
    setSelectedProvinceLayer(event.target);
    setMenuPosition(menuPos);
    setMenuVisible(true);
  };
  const handleMouseEnter = (event) => {
    event.target.setAttribute("fill", "#CCCCCC");
    const targetElement = event.target.parentNode;
    const boundingRect = targetElement.getBoundingClientRect();
    const province = targetElement.getAttribute("id");

    if (province === "West_Coast" || "North_Canterbury" || "South_Canterbury") {
      // Position tooltip at the center of the province element.
      setTooltipPosition({
        x: boundingRect.left + boundingRect.width / 2 + window.pageXOffset,
        y: boundingRect.top + boundingRect.height / 2 + window.pageYOffset,
      });
    } else {
      setTooltipPosition({
        x: boundingRect.x + window.pageXOffset,
        y: boundingRect.y + window.pageYOffset,
      });
    }

    const tooltipContent = province.replace(/_/g, " ");
    setTooltipContent(tooltipContent);
    setTooltipVisible(true);
  };

  const handleMouseLeave = (event) => {
    event.target.setAttribute("fill", "#fff");
    setTooltipVisible(false);
  };

